import React from 'react';
import Editor from '@monaco-editor/react';
import './App.css';

function App() {
  const placeholderCode = `// 从左侧选择一个文件来查看代码...
// 或者直接在这里编写代码进行分析！

function greet() {
  console.log("欢迎使用 AI Code Sentinel!");
}

greet();
`;

  return (
    <div className="App">
      <div className="file-explorer">
        <h2>文件浏览器</h2>
        {/* 文件树将在这里 */}
        <p style={{ color: '#888' }}>文件列表将显示在此处</p>
      </div>

      <div className="main-content">
        <button className="analyze-button">
          执行AI分析
        </button>
        <div className="editor-container">
          <Editor
            height="100%"
            defaultLanguage="javascript"
            theme="vs-dark"
            defaultValue={placeholderCode}
          />
        </div>
        <div className="analysis-panel">
          <h2>AI分析结果</h2>
          <p style={{ color: '#888' }}>分析结果将显示在此处</p>
        </div>
      </div>
    </div>
  );
}

export default App;
