// src/App.js (最终交互版)
import React, { useState, useEffect, useRef } from 'react';
import Editor from '@monaco-editor/react';
import axios from 'axios';
import FileExplorer from './components/FileExplorer';
import './App.css';

// 后端API的地址
const API_BASE_URL = 'http://localhost:3001';

function App() {
  // --- 状态管理 ---
  const [files, setFiles] = useState([]); // 文件列表
  const [activeFile, setActiveFile] = useState(null); // 当前选中的文件名
  const [code, setCode] = useState('// 从左侧选择一个文件，或在此处开始编写代码...'); // 编辑器内容
  const [analysisResults, setAnalysisResults] = useState([]); // AI分析结果
  const editorRef = useRef(null); // 编辑器实例的引用
  const decorationsRef = useRef([]); // 高亮装饰的引用

  // ---副作用钩子---
  // 1. 组件加载时，获取文件列表
  useEffect(() => {
    axios.get(`${API_BASE_URL}/api/files`)
      .then(response => {
        setFiles(response.data);
      })
      .catch(error => {
        console.error("获取文件列表失败:", error);
        alert("无法连接到后端服务，请确保后端已启动。");
      });
  }, []); // 空依赖数组表示只在初始加载时运行一次

  // 2. 当分析结果更新时，更新编辑器高亮
  useEffect(() => {
    if (editorRef.current && analysisResults.length > 0) {
      const newDecorations = analysisResults.map(result => ({
        range: new window.monaco.Range(result.line_start, 1, result.line_end, 1),
        options: {
          isWholeLine: true,
          className: `highlight-${result.risk_level.toLowerCase()}-risk`,
          hoverMessage: { value: `[${result.risk_level}] ${result.summary}` }
        }
      }));
      // 使用 deltaDecorations 更新高亮，它会移除旧的并添加新的
      decorationsRef.current = editorRef.current.deltaDecorations(decorationsRef.current, newDecorations);
    } else if (editorRef.current) {
      // 如果没有分析结果，则清空高亮
      decorationsRef.current = editorRef.current.deltaDecorations(decorationsRef.current, []);
    }
  }, [analysisResults]); // 依赖于 analysisResults


  // --- 事件处理函数 ---
  // 1. 处理文件点击事件
  const handleFileSelect = (filePath) => {
    // 增加这行：在加载新文件内容前，先清除旧的高亮
    if (editorRef.current) {
      decorationsRef.current = editorRef.current.deltaDecorations(decorationsRef.current, []);
    }

    axios.get(`${API_BASE_URL}/api/file-content?path=${filePath}`)
      .then(response => {
        setCode(response.data.content);
        setActiveFile(filePath);
        setAnalysisResults([]);
      })
      .catch(error => {
        console.error("获取文件内容失败:", error);
      });
  };

  // 2. 处理编辑器挂载
  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;
    window.monaco = monaco; // 将 monaco 实例挂载到 window，方便调试
  };

  // 3. 处理AI分析按钮点击
  const handleAnalyze = async () => {
    if (!editorRef.current) return;
    const currentCode = editorRef.current.getValue();
    if (!currentCode) {
      alert("代码不能为空！");
      return;
    }
    try {
      const response = await axios.post(`${API_BASE_URL}/api/analyze`, { code: currentCode });
      setAnalysisResults(response.data);
    } catch (error) {
      console.error("AI分析请求失败:", error);
      alert("AI分析失败，请检查后端服务和日志。");
    }
  };

  return (
    <div className="App">
      <div className="file-explorer">
        {/* 渲染文件浏览器组件 */}
        <FileExplorer files={files} onFileSelect={handleFileSelect} />
      </div>

      <div className="main-content">
        <button className="analyze-button" onClick={handleAnalyze}>
          执行AI分析
        </button>
        <div className="editor-container">
          <Editor
            height="100%"
            language={activeFile?.split('.').pop() || 'javascript'} // 根据文件后缀动态设置语言
            theme="vs-dark"
            value={code}
            onMount={handleEditorDidMount}
            onChange={(value) => setCode(value)} // 允许用户编辑
          />
        </div>
        <div className="analysis-panel">
          <h2>AI分析结果</h2>
          {analysisResults.length === 0 ? (
            <p style={{ color: '#888' }}>点击上方按钮开始分析...</p>
          ) : (
            analysisResults.map((result, index) => (
              <div key={index} className="analysis-item">
                <div className="analysis-item-header">
                  <span className={`risk-badge risk-${result.risk_level.toLowerCase()}`}>{result.risk_level}</span>
                  <strong>{result.summary}</strong>
                </div>
                <p><strong>行号:</strong> {result.line_start}-{result.line_end} | <strong>置信度:</strong> {result.confidence}%</p>
                <p><strong>建议:</strong> {result.suggestion}</p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
